    const [cards, setCards] = useState([
        {
            id: 1,
            name: "Rick Sanchez",
            source: "https://upload.wikimedia.org/wikipedia/en/a/a6/Rick_Sanchez.png"
        },
        {
            id: 2,
            name: "Morty Smith",
            source: "https://upload.wikimedia.org/wikipedia/en/a/a6/Rick_Sanchez.png"
        },
        {
            id: 3,
            name: "Jerry Smith",
            source: "https://upload.wikimedia.org/wikipedia/en/a/a6/Rick_Sanchez.png"
        },
        {
            id: 4,
            name: "Summer Smith",
            source: "https://upload.wikimedia.org/wikipedia/en/a/a6/Rick_Sanchez.png"
        },
        {
            id: 5,
            name: "Doofus Rick",
            source: "https://upload.wikimedia.org/wikipedia/en/a/a6/Rick_Sanchez.png"
        },
    ]
    );
    const [selections, setSelections] = useState([]);

    function imageClicked(id1)
    {
        setSelections([...selections,id1]);

        console.log(selections);
    }

    return(
        <div>
            <Header score={score} bestScore={bestScore}/>
            <hr></hr>
            <Content cards = { cards } imageClicked={ imageClicked }/>
        </div>
    );
}

export default App
