import React, { Component } from "react";

class App extends Component
{
    constructor(){
        super();

        this.state = {
            task: { text: '' },
            tasks: [],
        };
    }
    handleChange = (e) => {
        this.setState({
          task : {
            text: e.target.value,
          }
        });
      };

      onSubmitTask = (e) => {
        e.preventDefault();
        this.setState({
          tasks: this.state.tasks.concat(this.state.task),
          task: { text: '' },
        });
      };
    render()
    {
        const {task, tasks} = this.state;

        return(
            <div>
                <form>
                    <label htmlFor="inputfield">Enter task  <input type="text" id="inputfield" placeholder="hey"></input></label>
                    <button type="submit">Add Task</button>
                </form>
            </div>
        );
    }
}

export default App;
